import { Component, OnInit } from '@angular/core';
import { News } from '../../../models/news.model';

@Component({
  selector: 'app-image-slider',
  standalone: true,
  imports: [],
  templateUrl: './image-slider.component.html',
  styleUrl: './image-slider.component.css'
})
export class ImageSliderComponent implements OnInit{
  news: News[] = [
    {path: `data:image/jpeg;base64,`, description: 'دانشگاه صنعتی اصفهان از یک آزمایشگاه صنعتی جدید رونمایی کرد. تا ارتباط صنعت و دانشجویان را تقویت کند.'},
    {path:  `data:image/jpeg;base64,`, description: 'دانشگاه صنعتی اصفهان از یک آزمایشگاه صنعتی جدید رونمایی کرد. تا ارتباط صنعت و دانشجویان را تقویت کند.'},
    {path: 'assets/icons/search-icon.svg', description: 'دانشگاه صنعتی اصفهان از یک آزمایشگاه صنعتی جدید رونمایی کرد. تا ارتباط صنعت و دانشجویان را تقویت کند.'},
    {path: 'assets/menu.svg', description: 'دانشگاه صنعتی اصفهان از یک آزمایشگاه صنعتی جدید رونمایی کرد. تا ارتباط صنعت و دانشجویان را تقویت کند.'}
  ]
  currentNews: News = {path: 'assets/logos/iut-logo.svg', description: 'دانشگاه صنعتی اصفهان از یک آزمایشگاه صنعتی جدید رونمایی کرد. تا ارتباط صنعت و دانشجویان را تقویت کند.'};

  currentIndex: number = 0;

  ngOnInit(): void { this.startAutoSlide(); }

  startAutoSlide(): void { setInterval(() => { this.nextSlide(); }, 4000);}
  nextSlide(): void {
    this.currentIndex = (this.currentIndex + 1) % this.news.length; 
    this.currentNews = this.news[this.currentIndex];
  }
  prevSlide(): void {
     this.currentIndex = (this.currentIndex - 1 + this.news.length) % this.news.length;
     this.currentNews = this.news[this.currentIndex];
  }
}
